mapper.select:
select 
	cn_note_id as id,
	cn_note_title as title
from
	cn_note

 
	
	cn_user_id = #{userId } 
	
	
	and cn_notebook_id = #{notebookId } 
	
	
		
		and cn_note_title like #{title } 
		
		
		and cn_note_title like #{key } 
		
	
	
		or cn_note_body like #{key }
	
 


	limit #{start },#{rows }
